const Transform2D = require("./Transform2D");

class DisplayObject2D extends Transform2D
{
	constructor(){
		super();
		this.mouseEnabled = false;
	}

	get parent(){
		return this._parent;
	}

	set parent(value){
		this._parent = value;
	}

	onUpdate(timeElapsed){
		this.emit("enterFrame");
	}
	onDraw(gl, mode){}
	draw(gl, mode){
		this.onDraw(gl, mode);
	}
}

module.exports = DisplayObject2D;