const DisplayObject2D = require("./DisplayObject2D");

class DisplayObjectContainer2D extends DisplayObject2D
{
	constructor(){
		super();
		this._children = [];
		this.mouseChildren = true;
	}

	get numChildren(){
		return this._children.length;
	}

	isIndexValid(index){
		return index >= 0 && index < this.numChildren;
	}

	addChild(child){
		this.addChildAt(child, this.numChildren);
	}

	addChildAt(child, index){
		this._children[index] = child;
		child.parent = this;
	}

	onUpdate(timeElapsed){
		super.onUpdate(timeElapsed);
		for(var child of this._children){
			if(child.isVisible()){
				child.onUpdate(timeElapsed);
			}
		}
	}

	onDraw(gl, mode){
		for(var child of this._children){
			if(!child.isVisible()){
				continue;
			}
			child.draw(gl, mode);
		}
	}
}

module.exports = DisplayObjectContainer2D;