class MouseManager
{
	constructor(view3d){
		this.view3d = view3d;
		this.gl = view3d.gl;
		this.requestPick = false;
		this.init();
	}

	init(){
		const {canvas, gl} = this.view3d;
		canvas.addEventListener("mousemove", this.onMouseMove.bind(this));
		canvas.addEventListener("mousedown", this.onMouseDown.bind(this));
		canvas.addEventListener("mouseup"  , this.onMouseUp.bind(this));

		this.pickBuffer = gl.createRenderbuffer();
		gl.bindRenderbuffer(gl.RENDERBUFFER, this.pickBuffer);
		gl.renderbufferStorage(gl.RENDERBUFFER, gl.RGBA8, canvas.width, canvas.height);
		gl.bindRenderbuffer(gl.RENDERBUFFER, null);

		this.frameBuffer = gl.createFramebuffer();
		gl.bindFramebuffer(gl.FRAMEBUFFER, this.frameBuffer);
		gl.framebufferRenderbuffer(gl.FRAMEBUFFER, gl.COLOR_ATTACHMENT0, gl.RENDERBUFFER, this.pickBuffer);
		gl.bindFramebuffer(gl.FRAMEBUFFER, null);

		let buffer = new ArrayBuffer(4);
		this._pickData = new Uint8Array(buffer);
		this._pickValue = new Uint32Array(buffer);
	}

	update(){
		if(!this.requestPick){
			return;
		}
		this.requestPick = false;
		const {view3d, gl, mouseX, mouseY} = this;

		gl.bindFramebuffer(gl.FRAMEBUFFER, this.frameBuffer);
		
		gl.clearColor(0, 0, 0, 0);
		gl.clear(gl.COLOR_BUFFER_BIT);
		
		this._pickBegin();
		view3d.drawScene("pick");
		gl.readPixels(mouseX, view3d.height - mouseY, 1, 1, gl.RGBA, gl.UNSIGNED_BYTE, this._pickData);
		
		gl.bindFramebuffer(gl.FRAMEBUFFER, null);

		let mouseTarget = this.objectDict[this._pickValue[0]];
		console.log(mouseTarget, "click");
	}

	onMouseMove(evt){
		const {scene2d, scene3d} = this.view3d;
		scene2d.mouseX = evt.x;
		scene2d.mouseY = evt.y;
		//console.log(this, evt.x, evt.y);
	}

	onMouseDown(evt){
		console.log("down", evt.x, evt.y);
		this.requestPick = true;
		this.mouseX = evt.x;
		this.mouseY = evt.y;
	}

	onMouseUp(evt){
		console.log("up", evt.x, evt.y);
	}

	_pickBegin(){
		this.nextIndex = 0;
		this.objectDict = [];
	}

	pickRegister(target){
		this.nextIndex++;
		this.objectDict[this.nextIndex] = target;
		return this.nextIndex;
	}
}

module.exports = MouseManager;