const DisplayObjectContainer2D = require("./DisplayObjectContainer2D");

class Scene2D
{
	constructor(width, height){
		this.root = new DisplayObjectContainer2D();
		this.width = width;
		this.height = height;
	}

	update(timeElapsed){
		this.root.onUpdate(timeElapsed);
	}

	draw(gl, mode){
		let {root, width, height} = this;
		gl.render2d.drawBegin(width, height, mode);
		root.draw(gl, mode);
	}
}

module.exports = Scene2D;