"use strict";

const DisplayObject2D = require("./DisplayObject2D");

class Scene2D
{
	constructor(width, height){
		this.root = new DisplayObject2D();
		this.root._scene = this;
		this.width = width;
		this.height = height;
	}

	update(timeElapsed){
		this.root.onUpdate(timeElapsed);
	}

	draw(gl, mode){
		gl.render2d.mode = mode;
		gl.render2d.invalidate();
		this.root.draw(gl, mode);
		gl.render2d.submit();
	}
}

module.exports = Scene2D;