const DisplayObjectContainer2D = require("./DisplayObjectContainer2D");

class Scene2D
{
	constructor(){
		this.root = new DisplayObjectContainer2D();
	}

	update(timeElapsed){
		this.root.onUpdate(timeElapsed);
	}

	draw(gl, mode){
		gl.render2d.drawBegin(mode);
		this.root.draw(gl, mode);
	}
}

module.exports = Scene2D;