"use strict";

class Vector3D{
	static Interpolate(a, b, f, result){
		let t = 1 - f;
		result.x = a.x * t + b.x * f;
		result.y = a.y * t + b.y * f;
		result.z = a.z * t + b.z * f;
		return result;
	}

	constructor(x=0, y=0, z=0){
		this.x = x;
		this.y = y;
		this.z = z;
	}

	identity(){
		this.x = 0;
		this.y = 0;
		this.z = 0;
	}

	setTo(x, y, z){
		this.x = x;
		this.y = y;
		this.z = z;
	}

	copyFrom(other){
		this.x = other.x;
		this.y = other.y;
		this.z = other.z;
	}

	negate(result=this){
		result.x = - this.x;
		result.y = - this.y;
		result.z = - this.z;
		return result;
	}

	add(other, result=this){
		result.x = this.x + other.x;
		result.y = this.y + other.y;
		result.z = this.z + other.z;
		return result;
	}

	copyToArray(list, offset=0){
		list[offset  ] = this.x;
		list[offset+1] = this.y;
		list[offset+2] = this.z;
	}
}

module.exports = Vector3D;