class Vector3D
{
	constructor(x=0, y=0, z=0, w=1){
		this.x = x;
		this.y = y;
		this.z = z;
		this.w = w;
	}

	identity(){
		this.x = 0;
		this.y = 0;
		this.z = 0;
		this.w = 1;
	}

	setTo(x, y, z, w){
		this.x = x;
		this.y = y;
		this.z = z;
		this.w = w;
	}

	copyFrom(other){
		this.x = other.x;
		this.y = other.y;
		this.z = other.z;
		this.w = other.w;
	}

	negate(){
		this.x = - this.x;
		this.y = - this.y;
		this.z = - this.z;
	}

	add(other, result=null){
		if(null == result){
			result = this;
		}
		result.x = this.x + other.x;
		result.y = this.y + other.y;
		result.z = this.z + other.z;
		return result;
	}
}

module.exports = Vector3D;