"use strict";

const OpCode = require("./OpCode");

class OpFactory
{
	static JumpIf(offset)
	{
		return [OpCode.JUMP_IF, offset];
	}
	
	static Jump(offset)
	{
		return [OpCode.JUMP, offset];
	}
	
	static Push(value)
	{
		return [OpCode.PUSH, value];
	}
	
	static Invoke(argCount, retCount)
	{
		return [OpCode.INVOKE, argCount, retCount];
	}
	
	static Return(retCount)
	{
		return [OpCode.RETURN, retCount];
	}
	
	static GetVar(name)
	{
		return [OpCode.GET_VAR, name];
	}

	static SetVar(name)
	{
		return [OpCode.SET_VAR, name];
	}
	
	static NewFunction(jumpOffset, argList, userData)
	{
		return [OpCode.NEW_FUNCTION, jumpOffset, argList, userData];
	}
}

module.exports = OpFactory;