
const OpCode = require('./OpCode');

const JumpIf = (offset) => [OpCode.JUMP_IF, offset];
const Jump   = (offset) => [OpCode.JUMP,    offset];

const GetVar = (name) => [OpCode.GET_VAR, name];
const SetVar = (name) => [OpCode.SET_VAR, name];

const Invoke = (argCount, retCount) => [OpCode.INVOKE, argCount, retCount];
const Return = (retCount) => [OpCode.RETURN, retCount];

const Push = (value) => [OpCode.PUSH, value];
const NewFunction = (jumpOffset, argList, userData) => [OpCode.NEW_FUNCTION, jumpOffset, argList, userData];

module.exports = {
	JumpIf,
	Jump,
	GetVar,
	SetVar,
	Invoke,
	Return,
	Push,
	NewFunction
};
