"use strict";

const OpCode = require("./OpCode");

exports.JumpIf = (offset) => [OpCode.JUMP_IF, offset];
exports.Jump   = (offset) => [OpCode.JUMP,    offset];

exports.GetVar = (name) => [OpCode.GET_VAR, name];
exports.SetVar = (name) => [OpCode.SET_VAR, name];

exports.Invoke = (argCount, retCount) => [OpCode.INVOKE, argCount, retCount];
exports.Return = (retCount) => [OpCode.RETURN, retCount];

exports.Push = (value) => [OpCode.PUSH, value];
exports.NewFunction = (jumpOffset, argList, userData) => [OpCode.NEW_FUNCTION, jumpOffset, argList, userData];
