"use strict";

class ArduinoCmdDict
{
	constructor(){
		this.cmdDict = Object.create(null);
	}
	
	addCmd(cmd, handler){
		this.cmdDict[cmd] = handler;
	}
	
	hasCmd(cmd){
		return this.cmdDict[cmd] != null;
	}
	
	translate(output, cmd, argList, indent=0){
		let handler = this.cmdDict[cmd];
		return handler(output, cmd, argList, indent);
	}
}

module.exports = ArduinoCmdDict;