"use strict";

function castCodeToString(code, i, array)
{
	const len = array.length.toString().length;
	let result = code[0].padEnd(10);
	if(code.length > 1)
		result += JSON.stringify(code.slice(1)).slice(1, -1);
	return i.toString().padStart(len) + " " + result;
}

exports.castCodeListToString = codeList => codeList.map(castCodeToString).join("\n");