const confirmWrap = (self, method, title) => overrideMethod(self, method, oldFn => () => 
	antd.Modal.confirm({
		title,
		okText: '确定',
		cancelText: '取消',
		centered: true,
		maskClosable: true,
		onOk(){
			oldFn.call(self);
		},
	})
);

