class Dialog extends React.Component
{
	constructor(props){
		super(props);
		this.dialogRef = React.createRef();
	}

	showModal(callback){
		const dialog = this.dialogRef.current;
		dialog.addEventListener('close', () => {
			const form = dialog.firstElementChild;
			callback(dialog.returnValue, form.elements);
		}, {once:true});
		dialog.showModal();
	}
}

Dialog.prototype.render = createElement(function(tag, end){
	const {children, style, buttonList} = this.props;
	tag('dialog', {style, ref:this.dialogRef})
		tag('form', {method:'dialog'})
			tag('header')
				tag('span'), tag`X`, end('span')
			end('header')
			tag('main', {children}, true)
			tag('footer')
				for(let {title, ...props} of buttonList){
					tag('button', props), tag`${title}`, end('button')
				}
			end('footer')
		end('form')
	end('dialog')
});

const TabPanel = createElement(function(tag, end, {children}){
	tag('div', {className:'tab-panel', ref:initTabPanel, style:{paddingTop:'12px'}})
		tag('div')
			for(let i=0; i<children.length; ++i){
				const {title, className} = children[i].props;
				tag('span', {className}), tag`${title}`, end('span')
			}
		end('div')
		tag('div', {children}, true)
	end('div')
})

const Collapse = createElement(function(tag, end, {style, children}){
	tag('div', {className:'collapse', style})
		for(let i=0; i<children.length; ++i){
			const child = children[i];
			const {title, open} = child.props;
			tag('details', {open})
				tag('summary'), tag`${title}`, end('summary')
				tag(React.cloneElement(child, {title:null,open:null}))
			end('details')
		}
	end('div')
})

let workspace;
function initWorkspace(blocklyDiv){
	workspace = Blockly.inject(blocklyDiv, {
		horizontalLayout: false,
		toolboxPosition: 'start',
		media: 'media/',
		toolbox: updateToolBoxByTag(Blockly.Xml.textToDom(defaultToolbox), searchInfo.board),
		grid: {
			spacing: 20,
			length: 3,
			colour: '#fff',
			snap: true
		},
		zoom: {
			controls: false,
			wheel: false,
			startScale: 1.0,
			maxScale: 1.2,
			minScale: 0.8,
		},
		trashcan: false,
		renderer: 'zelos',
		theme: require('./scratch_theme'),
	});

	Blockly.Xml.domToWorkspace(Blockly.Xml.textToDom(defaultXml), workspace);

	//workspace.addChangeListener(Blockly.Events.disableOrphans);
	workspace.addChangeListener(function(evt){
		if(evt.type != Blockly.Events.BLOCK_DELETE)return;
		let div = document.getElementById('toolbox_mask');
		div.style.display = 'none';
	});

	const onResize = () => Blockly.svgResize(workspace);
	blocklyDiv.addEventListener('active', onResize);
}

function initCodeMirror(parentDiv){
	let code = `#include<WeELFMini.h>\nvoid setup(){\n\tint a = 10;\n}\n\nvoid loop(){\n}`;
	let editor = monaco.editor.create(parentDiv, {
		model: monaco.editor.createModel(code, 'cpp')
	});
	function onResize(){
		let width = window.innerWidth - 400;
		let height = window.innerHeight - 80;
		editor.layout({width, height});
	}
	parentDiv.addEventListener('active', onResize);
	window.addEventListener('resize', onResize);
}

