function getContext(canvas){
	let ctx = canvas.getContext('2d', {alpha:false});
	ctx.imageSmoothingQuality = 'high';
	ctx.textBaseline = 'bottom';
	return ctx;
}

function run(fn){
	requestAnimationFrame(function loop(timeElapsed){
		requestAnimationFrame(loop);
		fn(timeElapsed);
	});
}

