
exports.Engine = require('./src/Engine');
exports.Entity = require('./src/Entity');
exports.ComponentSelector = (...args) => Object.freeze(args);
exports.forEachEntityDo = (entitySet, onAdded, onRemoved) => {
	entitySet.forEach(onAdded);
	if(onAdded)
		entitySet.events.on('added', onAdded);
	if(onRemoved)
		entitySet.events.on('removed', onRemoved);
};