"use strict";

const decodeBmp = require("./bmp");
const decodeTga = require("./tga");
function decode(name, bytes){
	let index = name.lastIndexOf(".");
	let ext = name.slice(index+1);
	switch(ext){
		case "tga": return decodeTga(bytes);
		case "bmp": return decodeBmp(bytes);
	}
	throw new Error(`${name} not support yet!`);
}

module.exports = {
	decodeTga,
	decodeBmp,
	decode
};
