
const castAttribute = ([k, v]) => ` ${k}="${v}"`;

function create(args){
	if(!Array.isArray(args))
		return args;
	let [tagName, ...children] = args;
	let attributes = (children[0] == null || children[0].constructor == Object) && children.shift();
	let result = ["<", tagName];
	if(attributes)
		   result.push(...Object.entries(attributes).map(castAttribute));
	if(children.length > 0)
		   result.push(">", ...children.map(create), "</", tagName, ">");
	else   result.push("/>");
	return result.join("");
}

export default create;