const INSIDE	 = -1;
const CROSS	 = 0;
const OUTSIDE	 = 1;

/**
 * 判断pd是否在射线ab和ac之间
 */		
function Judge(pa, pb, pc, pd){
	var ab = pb.subtract(pa);
	var ac = pc.subtract(pa);
	var ad = pd.subtract(pa);
	
	var det = crossProd(ab, ad) * crossProd(ac, ad);
	
	if (nearEquals(det, 0)) return CROSS;
	
	if(det < 0){
		var bc = pc.subtract(pb);
		if(crossProd(bc, ac) * crossProd(bc, ad) > 0){
			return INSIDE;
		}
	}
	
	return OUTSIDE;
}