
const INSIDE	 = -1;
const CROSS	 = 0;
const OUTSIDE	 = 1;
		
function Judge(pt, pa, pb, pc){
	var v0 = pc.subtract(pa);
	var v1 = pb.subtract(pa);
	var v2 = pt.subtract(pa);
	
	var dot00 = dotProd(v0, v0);
	var dot01 = dotProd(v0, v1);
	var dot02 = dotProd(v0, v2);
	var dot11 = dotProd(v1, v1);
	var dot12 = dotProd(v1, v2);
	
	var factor = 1 / (dot00 * dot11 - dot01 * dot01);
	
	var u = (dot11 * dot02 - dot01 * dot12) * factor;
	
	if (u < 0 || 1 < u) return OUTSIDE;
	
	var v = (dot00 * dot12 - dot01 * dot02) * factor;
	
	if (v < 0 || 1 < v) return OUTSIDE;
	
	if(0 == u || 0 == v || nearEquals(u + v, 1)){
		return CROSS;
	}
	
	return (u + v < 1) ? INSIDE : OUTSIDE;
}