function getProjectPtOnLine(pt, pa, pb){
	if(pa.x == pb.x)return {x: pa.x, y: pt.y};
	if(pa.y == pb.y)return {x: pt.x, y: pa.y};
	const k = (pb.y - pa.y) / (pb.x - pa.x);
	const projectX = pa.x + (k * (pt.y - pa.y) + pt.x - pa.x) / (k * k + 1);
	const projectY = pa.y + k * (projectX - pa.x);
	return {x: projectX, y: projectY};
}

