
module.exports = class{
	constructor(){
		this.list = [];
	}

	empty(){
		return this.list.length == 0;
	}

	add(node){
		this.list.push(node);
	}

	first(){
		return this.list[0];
	}

	next(){
		return this.list.shift();
	}

	expect(nodeType){
		return this.first().type == nodeType;
	}

	accept(nodeType){
		if(!this.expect(nodeType)){
			throw new Error("this nodeType is not the expect one!");
		}
		return this.next();
	}
	
	matchAny(nodeTypes){
		return nodeTypes.includes(this.first().type);
	}
}