'use strict';

const collectArgs = node => node.arguments.map(walk);
const getProperty = node => node.computed ? '[' + walk(node.property) + ']' : '.' + node.property.name;

const addTab = ((...args) => code => code.replace(...args))(/^/gm, ' '.repeat(4));

const handler = {
	ImportDeclaration(node){
		if(node.specifiers.length <= 0){
			return `import ${this(node.source)}`;
		}
		let specifiers;
		let a = node.specifiers.filter(v => v.type != 'ImportSpecifier').map(this);
		let b = node.specifiers.filter(v => v.type == 'ImportSpecifier').map(this);
		if(a.length > 0 && b.length > 0){
			specifiers = a.join(', ') + ', {' + b.join(', ') + '}';
		}else if(a.length > 0){
			specifiers = a.join(', ');
		}else if(b.length > 0){
			specifiers = '{' + b.join(', ') + '}';
		}
		return `import ${specifiers} from ${this(node.source)}`;
	},
	ImportDefaultSpecifier(node){
		return this(node.local);
	},
	ImportNamespaceSpecifier(node){
		return '* as ' + this(node.local);
	},
	ImportSpecifier(node){
		let name = this(node.imported);
		let alias = this(node.local);
		return name == alias ? name : `${name} as ${alias}`;
	},
	ExportNamedDeclaration(node){
		let result = 'export ';
		if(node.declaration){
			result += this(node.declaration);
		}
		if(node.specifiers.length > 0){
			result += '{' + node.specifiers.map(this).join(', ') + '}';
		}
		if(node.source){
			result += ' from ' + this(node.source);
		}
		return result;
	},
	ExportDefaultDeclaration(node){
		return 'export derault ' + this(node.declaration);
	},
	ExportSpecifier(node){
		let name = this(node.local);
		let alias = this(node.exported);
		return name == alias ? name : `${name} as ${alias}`;
	},
	ExportAllDeclaration(node){
		return 'export * from ' + this(node.source);
	},
	ClassDeclaration(node){
		return `class ${walk(node.id)}${node.superClass ? ' extends ' + walk(node.superClass) : ''} {\n${walk(node.body)}\n}`;
	},
	ClassBody(node){
		return addTab(handler.Program(node));
	},
	MethodDefinition(node){
		let prefix = node.static ? 'static ' : '';
		console.log(node);
		let key = walk(node.key);
		if(node.computed){
			key = '[' + key + ']';
		}
		if(node.value.type == 'FunctionExpression'){
			return prefix + handler.FunctionExpression(node.value, key);
		}
		return prefix + walk(node.key) + ':' + walk(node.value);
	},
	Program(node){
		return node.body.map(walk).join('\n');
	},
	BlockStatement(node){
		return handler.Program(node);
	},
	Identifier(node){
		return node.name;
	},
	FunctionDeclaration(node){
		return handler.FunctionExpression(node);
	},
	ArrowFunctionExpression(node){
		const body = this(node.body);
		let params;
		if(node.params.length == 1 && node.params[0].type == 'Identifier'){
			params = this(node.params[0]);
		}else{
			params = `(${node.params.map(this)})`;
		}
		const result = params + ` => `;
		if(node.expression){
			return result + body;
		}
		return result + `{\n${addTab(body)}\n}`;
	},
	FunctionExpression(node, method){
		const body = walk(node.body);
		return `${node.async ? 'async ': ''}${method || 'function'}${node.generator ? '*' : ' '}${node.id ? node.id.name : ''}(${node.params.map(walk)}){\n${addTab(body)}\n}`;
	},
	AwaitExpression(node){
		return `await ` + this(node.argument);
	},
	YieldExpression(node){
		return `yield` + (node.delegate ? '*' : ' ') + this(node.argument);
	},
	ExpressionStatement(node){
		return this(node.expression) + ';';
	},
	CallExpression(node){
		return this(node.callee) + '(' + collectArgs(node) + ')';
	},
	SpreadElement(node){
		return '...' + this(node.argument);
	},
	RestElement(node){
		return '...' + this(node.argument);
	},
	MemberExpression(node){
		return this(node.object) + getProperty(node);
	},
	EmptyStatement(node){
		return '';
	},
	UnaryExpression(node){
		return node.operator + ' ' + this(node.argument);
	},
	BinaryExpression(node){
		return this(node.left) + ' ' + node.operator + ' ' + this(node.right);
	},
	AssignmentExpression(node){
		return handler.BinaryExpression.call(this, node);
	},
	LogicalExpression(node){
		return handler.BinaryExpression.call(this, node);
	},
	Literal(node){
		return node.raw;
	},
	VariableDeclaration(node){
		return node.kind + ' ' + node.declarations.map(this);
	},
	VariableDeclarator(node){
		if(node.init){
			return this(node.id) + ' = ' + this(node.init);
		}
		return this(node.id);
	},
	ReturnStatement(node){
		if(node.argument){
			return 'return ' + this(node.argument);
		}
		return 'return';
	},
	ConditionalExpression(node){
		return this(node.test) + ' ? ' + this(node.consequent) + ' : ' + this(node.alternate);
	},
	IfStatement(node){
		let result = `if(${this(node.test)}){\n` + addTab(this(node.consequent)) + `\n}`;
		if(node.alternate){
			return result + `else{\n` + addTab(this(node.alternate)) + '\n}';
		}
		return result;
	},
	WhileStatement(node){
		return `while(${this(node.test)}){\n${addTab(this(node.body))}\n}`;
	},
	DoWhileStatement(node){
		return `do{\n${addTab(this(node.body))}\n}while(${this(node.test)});`;
	},
	ForStatement(node){
		return `for(${this(node.init)}${this(node.test)};${this(node.update)}){\n${addTab(this(node.body))}\n}`;
	},
	ForInStatement(node){
		return `for(${this(node.left)} in ${this(node.right)}){\n${addTab(this(node.body))}\n}`;
	},
	ForOfStatement(node){
		return `for(${this(node.left)} of ${this(node.right)}){\n${addTab(this(node.body))}\n}`;
	},
	BreakStatement(node){
		return node.label ? 'break ' + node.label : 'break';
	},
	ContinueStatement(node){
		return node.label ? 'continue ' + node.label : 'continue';
	},
	LabeledStatement(node){
		return this(node.label) + ':\n' + this(node.body);
	},
	UpdateExpression(node){
		let result = this(node.argument);
		if(node.prefix){
			return node.operator + result;
		}
		return result + node.operator;
	},
	ArrayExpression(node){
		return '[' + node.elements.map(this) + ']';
	},
	ObjectExpression(node){
		return '{' + node.properties.map(v => (v.key.type =='Identifier' ? this(v.key) : `[${this(v.key)}]`) + ':' + this(v.value)) + '}';
	},
	NewExpression(node){
		return 'new ' + handler.CallExpression.call(this, node);
	},
	DebuggerStatement(node){
		return 'debugger';
	},
	ThisExpression(node){
		return 'this';
	},
	Super(node){
		return 'super';
	},
	SequenceExpression(node){
		return '(' + node.expressions.map(this) + ')';
	},
	SwitchStatement(node){
		const body = addTab(node.cases.map(this).join('\n'));
		return `switch(${this(node.discriminant)}){\n${body}\n}`;
	},
	SwitchCase(node){
		const body = addTab(node.consequent.map(this).join('\n'));
		if(node.test){
			return `case ${this(node.test)}:\n${body}`;
		}
		return `default:\n${body}`;
	},
	ThrowStatement(node){
		return 'throw ' + this(node.argument);
	},
	TryStatement(node){
		let result = `try{\n${addTab(this(node.block))}\n}`;
		if(node.handler){
			result += this(node.handler);
		}
		if(node.finalizer){
			result += `finally{\n${addTab(this(node.finalizer))}\n}`;
		}
		return result;
	},
	CatchClause(node){
		return `catch(${this(node.param)}){\n${addTab(this(node.body))}\n}`;
	},
	WithStatement(node){
		return `with(${walk(node.object)}){\n${addTab(walk(node.body))}\n}`;
	},
	TemplateLiteral(node){
		return '`' + this(node.quasis[0]) + node.expressions.map((v, i) => '${' + this(v) + '}' + this(node.quasis[i+1])).join('') + '`';
	},
	TemplateElement(node){
		return node.value.raw;
	},
	TaggedTemplateExpression(node){
		return this(node.tag) + this(node.quasi);
	}
};

function walk(node){
	return handler[node.type].call(walk, node);
}

module.exports = walk;
