'use strict';

function needBrackets(calcPrecedence, isRightAssociativity){
	return function(innerOP, outerOP, isRightOperand){
		const innerOrder = calcPrecedence(innerOP);
		const outerOrder = calcPrecedence(outerOP);
		if(innerOrder != outerOrder)return innerOrder < outerOrder;
		return Boolean(isRightOperand) != isRightAssociativity(innerOP);
	}
}

module.exports = needBrackets;
