'use strict';

/*smaller is higher, calcPrecedence*/
function needBrackets(rightAssociativity){
	return function(innerOrder, outerOrder, isRightOperand){
		if(innerOrder != outerOrder)return innerOrder > outerOrder;
		return Boolean(isRightOperand) != rightAssociativity.includes(innerOrder);
	}
}

module.exports = needBrackets;
