"use strict";

const TextureMaterial = require("./TextureMaterial");
const {RenderState} = require("../renderers");

class TextureHitTestMaterial extends TextureMaterial{
	constructor(texture){
		super(texture);
		this.shader = "shader3d&pick";
		this.isPickMode = true;
		this.addressInstanceID = 24;
		this.renderState = RenderState.TextureHitTest;
	}
}

module.exports = TextureHitTestMaterial;