'use strict';

module.exports = class {
	constructor(realInjector, klass){
		this.realInjector = realInjector;
		this.klass = klass;
	}
	getValue(){
		if(!this.hasOwnProperty('value')){
			this.value = this.klass();
			this.realInjector.inject(this.value);
			this.realInjector = null;
			this.klass = null;
		}
		return this.value;
	}
}
