
export default module => next => msg => {
	let {name, data, from} = msg;
	from = msg.isFromInternal() ? from : from.name;
	console.groupCollapsed('%c%s.notify(%s, %o) called by %s', 'color: blue;', module.name, name.valueOf(), data, from);
	let result = next(msg);
	console.groupEnd();
	return result;
}