import Module from './Module';

class Msg
{
	_defaultPreventedFlag = false;
	_processCanceledFlag = false;

	constructor(name, data, from){
		Object.defineProperty(this, "name", {value:name});
		Object.defineProperty(this, "data", {value:data});
		Object.defineProperty(this, "from", {value:from});
	}

	cancelProcess(){
		this._processCanceledFlag = true;
	}

	isProcessCanceled(){
		return this._processCanceledFlag;
	}

	preventDefault(){
		this._defaultPreventedFlag = true;
	}

	isDefaultPrevented(){
		return this._defaultPreventedFlag;
	}

	isFrom(...args){
		return args.some(v => v === this.form);
	}

	isFromInternal(){
		return !(this.from instanceof Module);
	}
}

export default Msg;
