
class Msg
{
	_defaultPreventedFlag = false;
	_processCanceledFlag = false;

	constructor(name, data){
		Object.defineProperty(this, "name", {value:name});
		Object.defineProperty(this, "data", {value:data});
		checkMsgNameType(name);
	}

	cancelProcess(){
		this._processCanceledFlag = true;
	}

	isProcessCanceled(){
		return this._processCanceledFlag;
	}

	preventDefault(){
		this._defaultPreventedFlag = true;
	}

	isDefaultPrevented(){
		return this._defaultPreventedFlag;
	}
}

function checkMsgNameType(msgName){
	switch(typeof msgName.valueOf()){
		case "symbol":
		case "string":
			return;
	}
	console.assert(false, "msg.name must be 'symbol' or 'string'!", msgName);
}

export default Msg;
