export async function resolve(specifier, parentModuleURL, defaultResolver){
	if(!parentModuleURL){
		return {url:`file://${specifier}`, format: 'esm'};
	}
	let info = defaultResolver(specifier, parentModuleURL);
	if(!info.url.includes(':/node_modules/') && info.format === 'cjs'){
		info.format = 'esm';
	}
	return info;
}