"use strict";

const WebGL = require('../WebGL');
const DisplayObject2D = require("../DisplayObject2D");

class Bitmap extends DisplayObject2D
{
	constructor(){
		super();
		this._texture = null;
		this.width = 0;
		this.height = 0;
	}

	get texture(){
		return this._texture;
	}

	set texture(value){
		this._texture = value;
		if(value == null){
			return;
		}
		this.width = value.width;
		this.height = value.height;
	}

	onDraw(mode){
		let {render2d} = WebGL;
		if(this._texture || this.fgColor.a > 0){
			render2d.drawBitmap(this);
		}
	}
}

module.exports = Bitmap;