'use strict';

const WebGL = require('../WebGL');
const DisplayObject2D = require('../DisplayObject2D');

class Label extends DisplayObject2D
{
	constructor(){
		super();
	}

	get text(){
		return this._text;
	}

	set text(value){
		this._text = value;
	}

	onDraw(mode){
		if(!this._text)return;
		const {render2d, textMgr} = WebGL;
		for(let bitmap of textMgr.getTextBitmap(this.worldTransform, this._text)){
			render2d.drawBitmap(bitmap);
		}
	}
}

module.exports = Label;
