"use strict";

const WebGL = require('./WebGL');
const DisplayObject2D = require("./DisplayObject2D");

class Scene2D
{
	constructor(width, height){
		this.root = new DisplayObject2D();
		this.root._scene = this;
		this.width = width;
		this.height = height;
	}

	update(){
		this.root.onUpdate();
	}

	draw(mode){
		let {render2d} = WebGL;
		render2d.mode = mode;
		render2d.invalidate();
		this.root.draw(mode);
		render2d.submit();
	}
}

module.exports = Scene2D;