'use strict';

const self = this;

this.device = null;
this.commandEncoder = null;
this.renderPassEncoder = null;
this.textureBindGroupLayout = null;

this.createTextureBindGroupLayout = function(binding=0){
	const {device} = self;
	return device.createBindGroupLayout({
		bindings:[{
			binding,
			visibility: GPUShaderStage.FRAGMENT,
			type: 'sampled-texture'
		}]
	});
}

this.createTextureBindGroup = function(layout, textureView, binding=0){
	const {device} = self;
	return device.createBindGroup({
		layout,
		bindings: [{
			binding,
			resource: textureView
		}]
	});
}
