
const self = exports;

self.drawScreen = function(){
	const {gl} = self;
	gl.drawArrays(gl.TRIANGLE_STRIP, 0, 4);
}

self.loadImage = async function(path){
	let image = new Image();
	image.src = path;
	await image.decode();
	return image;
}

self.createTextureArray = function(){
	let canvas = document.createElement('canvas');
	let context = canvas.getContext('2d');
	return async function(width, height, pathList){
		const depth = pathList.length;
		const {gl} = self;
		let texture = gl.createTexture();
		gl.bindTexture(gl.TEXTURE_2D_ARRAY, texture);
		gl.texStorage3D(gl.TEXTURE_2D_ARRAY, 1, gl.RGB8, width, height, depth);
		gl.texParameteri(gl.TEXTURE_2D_ARRAY, gl.TEXTURE_MIN_FILTER, gl.NEAREST);
		for(let i=0; i<depth; ++i){
			let image = await self.loadImage(pathList[i]);
			if(image.width != width){
				if(canvas.width != width){
					canvas.width = width;
				}
				if(canvas.height != height){
					canvas.height = height;
				}
				context.drawImage(image, 0, 0, width, height);
				image = canvas;
			}
			gl.texSubImage3D(gl.TEXTURE_2D_ARRAY, 0, 0, 0, i, width, height, 1, gl.RGB, gl.UNSIGNED_BYTE, image);
		}
		return texture;
	}
}()

self.getImageJpegData = function(){
	let canvas = document.createElement('canvas');
	let context = canvas.getContext('2d');
	return async function(path){
		let image = await self.loadImage(path);
		let {width, height} = image;
		if(canvas.width != width){
			canvas.width = width;
		}
		if(canvas.height != height){
			canvas.height = height;
		}
		context.drawImage(image, 0, 0);
		return context.getImageData(0, 0, width, height);
	}
}()
