
exports.SolidColor = function(r=0, g=0, b=0, a=0){
	return function(){
		let {COLOR_BUFFER_BIT, DEPTH_BUFFER_BIT} = this;
		this.clearColor(r, g, b, a);
		this.clear(COLOR_BUFFER_BIT | DEPTH_BUFFER_BIT);
	};
}

exports.Depth = function(depth=1){
	return function(){
		let {DEPTH_BUFFER_BIT} = this;
		this.clearDepth(depth);
		this.clear(DEPTH_BUFFER_BIT);
	};
}

exports.Nothing = () => {};