'use strict';

const WebGL = require('../WebGL');
const Filter2D = require('./Filter2D');
const {ProgramName} = require('../const');

class BlurFilter extends Filter2D
{
	constructor(radius=4){
		super();
		this.shader = ProgramName.filter2d_blur;
		this.radius = radius;
	}

	onDraw(image, address){
		let {gl, drawScreen, render2d, renderStateStack, renderTargetMgr} = WebGL;
		const {radius} = this;
		let weight = calcWeight(radius);

		gl.uniform1i( address.radius, radius);
		gl.uniform1fv(address['weight[0]'], weight);

		offset.length = radius << 1;
		offset.fill(0);
		let dx = 1 / render2d.width;
		let dy = 1 / render2d.height;

		let filterFrameBuffer = renderTargetMgr.borrowFrameBuffer();
		gl.bindTexture(gl.TEXTURE_2D, image);

		for(let i=1; i<radius; ++i){
			offset[i*2] = i * dx;
		}
		gl.uniform2fv(address['offset[0]'], offset);
		drawScreen();

		renderStateStack.load(gl.DRAW_FRAMEBUFFER_BINDING);
		gl.bindTexture(gl.TEXTURE_2D, filterFrameBuffer.colorBuffer);
		
		for(let i=1; i<radius; ++i){
			offset[i*2] = 0;
			offset[i*2+1] = i * dy;
		}
		gl.uniform2fv(address['offset[0]'], offset);
		drawScreen();

		renderTargetMgr.returnFrameBuffer(filterFrameBuffer);
	}
}

const offset = [];

const calcWeight = (function(){
	const c1 = Math.sqrt(2 * Math.PI);
	const list = [];
	return radius => {
		list.length = radius;
		const sigma = radius / 2.57;
		const deno =  1 / (sigma * c1);
		const nume = -1 / (sigma * sigma * 2);
		list[0] = deno;
		let sum = deno;
		for(let i=1; i<radius; ++i){
			let v = deno * Math.exp(nume * i * i);
			list[i] = v;
			sum += 2 * v;
		}
		let factor = 1 / sum;
		for(let i=0; i<radius; ++i){
			list[i] *= factor;
		}
		return list;
	};
})();

module.exports = BlurFilter;