"use strict";

const TextureMaterial = require("./TextureMaterial");
const {RenderState} = require("../renderers");
const {delayNew} = require("utils/cache");
const {ProgramName} = require('../const');

class DepthMaterial extends TextureMaterial{
	constructor(){
		super();
		this.renderState = RenderState.TextureHitTest;
	}
}

Object.defineProperty(DepthMaterial, 'shader', {value: ProgramName.shader3d_depth});

DepthMaterial.new = delayNew(() => new DepthMaterial());

module.exports = DepthMaterial;