"use strict";

const {delayNew} = require('utils/cache');
const TextureMaterial = require('./TextureMaterial');
const {RenderState} = require('../renderers');
const {ProgramName} = require('../const');

class TextureHitTestMaterial extends TextureMaterial{
	constructor(){
		super();
		this.shader = ProgramName.shader3d_picking;
		//this.renderState = RenderState.TextureHitTest;
	}

	isTransparent(){
		return false;
	}
}

TextureHitTestMaterial.renderState = RenderState.TextureHitTest;
TextureHitTestMaterial.new = delayNew(() => new TextureHitTestMaterial());
TextureHitTestMaterial.bindTexture = (gl, texture) => {};

module.exports = TextureHitTestMaterial;