"use strict";

const Material = require("./Material");
const {RenderState} = require("../renderers");
const WebGL = require('../WebGL');
const {ProgramName} = require('../const');

class TextureMaterial extends Material{
	constructor(texture){
		super(ProgramName.shader3d_normal);
		this.texture = texture;
		this.renderState = RenderState.Texture;
	}

	onDraw(drawUnit, index, batchIndex){
		let {vao:{boneCount, bindCount}, boneMatrixList} = drawUnit;
		let {gl} = WebGL;
		let program = gl.getParameter(gl.CURRENT_PROGRAM);
		gl.uniform4fv(gl.getUniformLocation(program, `boneList[${index * boneCount}]`), boneMatrixList);
		if(index == 0 && batchIndex == 0){
			gl.uniform1i(gl.getUniformLocation(program, 'bindCount'), bindCount);
			gl.uniform1i(gl.getUniformLocation(program, 'boneCount'), boneCount);
		}
	}
}

module.exports = TextureMaterial;