
const DisplayObject3D = require('../DisplayObject3D');
const TerrainVertexArray = require('../vertexarrays/TerrainVertexArray');

class Terrain extends DisplayObject3D
{
	constructor(material){
		super();
		this.vao = new TerrainVertexArray();
		this.material = material;
		this.size = 4;
		this.uvScale = this.size / 128;
	}

	collectDrawUnits(collector){
		collector.addDrawUnit(this);
	}
}

module.exports = Terrain;
