"use strict";

const DisplayObject3D = require("../DisplayObject3D");
const {QuadVertexArray} = require("../vertexarrays");

class TerrainQuad
{
	constructor(size, tileX, tileY){
		this.vao = QuadVertexArray.new();
		this.size = size;
		this.tileX = tileX;
		this.tileY = tileY;
	}
}

module.exports = TerrainQuad;