"use strict";

class Texture2D{
	constructor(rawTexture){
		this.rawTexture = rawTexture;
		this.width = rawTexture.width;
		this.height = rawTexture.height;
		this.xyuvMul = [1, 1, 1, 1];
		this.xyuvAdd = [0, 0, 0, 0];
		this.scale9grid = [0, 0, 0, 0];//left, right, top, bottom
	}

	getRawAsset(){
		return this.rawTexture;
	}
}

module.exports = Texture2D;
