
const UniformBuffer = require('./UniformBuffer');

class MvpUniformBuffer extends UniformBuffer
{
	constructor(){
		super(0, 7, 128);
	}

	update(camera){
		this.setFloatv(0, camera.lens);
		camera.worldTransformInvert.copyToArray(this.floatView, 16);
		this.setFloatv(24, camera.rect);
	}
}

module.exports = MvpUniformBuffer;