"use strict";

const WebGL = require('../WebGL');

class QuadVertexArray
{
	static new(){
		return instance;
	}

	constructor(){
		this.maxCountPerDraw = 1024;
	}

	active(){
	}

	draw(instanceCount){
		let {gl} = WebGL;
		gl.drawArraysInstanced(gl.TRIANGLE_STRIP, 0, 4, instanceCount);
	}
}

const instance = new QuadVertexArray();

module.exports = QuadVertexArray;