"use strict";

class Program
{
	constructor(shader){
		this.shader = shader;
	}

	active(gl){
		if(this.program){
			gl.useProgram(this.program);
		}else{
			let program = gl.programMgr.fetch(this.shader);
			gl.useProgram(program);
			this.onInit(gl, program);
			this.program = program;
		}
	}

	onInit(gl, program){}
}

module.exports = Program;