"use strict";

const Program = require("./Program");

class ShadowProgram extends Program
{
	static new(){
		return instance;
	}

	constructor(){
		super("shadow/");
	}

	onInit(gl, program){
		gl.uniform1i(gl.getUniformLocation(program, "sampler0"), 1);
	}
}

const instance = new ShadowProgram();

module.exports = ShadowProgram;