
function combineReducers(reducers)
{
	return function(currState={}, action){
		const nextState = {};
		let isDirty = false;
		for(let key of Object.keys(reducers)){
			let currKeyState = currState[key];
			let nextKeyState = reducers[key](currKeyState, action);
			nextKeyState = nextKeyState === undefined ? currKeyState : nextKeyState
			nextState[key] = nextKeyState;
			isDirty = isDirty || nextKeyState !== currKeyState;
		}
		return isDirty ? nextState : currState;
	};
}

export default combineReducers;